INSERT INTO `toutiao_config_info` VALUES (1,2,'京港澳高速大荆段连环车祸爆炸 消防官兵连夜处置','2016-03-21-10-27-04-14585560241344-6760.jpg','/site/fun',1,''),(2,2,'京港澳高速大荆段连环车祸爆炸 消防官兵连夜处置','2016-03-21-10-27-17-14585560377924-6237.jpg','/site/fun',2,''),(3,2,'京港澳高速大荆段连环车祸爆炸 消防官兵连夜处置','2016-03-21-10-27-29-14585560496849-5233.jpg','/site/fun',3,'3小时前'),(5,1,'福田出大招了！首款EST超级卡车广州发布','2016-03-21-10-10-12-14585550125531-2354.jpg','http://louzhe-toutiao.lianjievip.com/site/detail?id=18',2,''),(4,1,'技术革命！美国公司研发36吨电动卡车 续航里程640公里','2016-03-21-04-18-57-14585339377307-3059.jpg','http://louzhe-toutiao.lianjievip.com/site/detail?id=23',2,'');
